import { FILECOIN, NEAR, ALGORAND, TEZOS, SUBSTRATE, ETHEREUM, MAILCHAIN } from './protocols/consts.js';

function casingByProtocol(value, protocol) {
    switch (protocol) {
        case MAILCHAIN:
            return value.toLowerCase(); // case insensitive
        case ETHEREUM:
            return value.toLowerCase(); // case insensitive
        case SUBSTRATE:
        case TEZOS:
            return value; // substrate encoding is case sensitive
        case ALGORAND:
            return value.toLowerCase(); // case insensitive
        case NEAR:
            return value.toLowerCase();
        case FILECOIN:
            return value.toLowerCase(); // base32, case insensitive
        default:
            throw new Error(`casing for protocol [${protocol}] not defined`);
    }
}

export { casingByProtocol };
