import { isHex } from '@mailchain/encoding';

function validateNearAccountId(accountId) {
    return validateNearImplicitAccount(accountId) || validateNearNamedAccount(accountId);
}
function validateNearImplicitAccount(address) {
    return address.length == 64 && isHex(address);
}
function validateNearNamedAccount(address) {
    return (address.length >= 2 &&
        address.length <= 64 &&
        /^(([a-z\d]+[-_])*[a-z\d]+\.)*([a-z\d]+[-_])*[a-z\d]+$/.test(address));
}

export { validateNearAccountId, validateNearImplicitAccount, validateNearNamedAccount };
