'use strict';

class ProtocolNotSupportedError extends Error {
    constructor(protocol) {
        super(`[${protocol}] is an unsupported protocol. Try again with a different protocol.`);
        Object.defineProperty(this, "protocol", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: protocol
        });
        Object.defineProperty(this, "type", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: 'protocol_unsupported'
        });
        Object.defineProperty(this, "docs", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: 'https://docs.mailchain.com/developer/errors/codes#protocol_unsupported'
        });
    }
}

exports.ProtocolNotSupportedError = ProtocolNotSupportedError;
