import { secureRandom } from '../../rand.js';
import { ED25519PublicKey } from '../../ed25519/public.js';
import { ED25519PrivateKey } from '../../ed25519/private.js';
import { ED25519KeyExchange } from './ed25519.js';

function fromPublicKey(publicKey, rand = secureRandom) {
    switch (publicKey.constructor) {
        case ED25519PublicKey:
            return new ED25519KeyExchange(rand);
        default:
            throw RangeError('unknown public key type');
    }
}
function fromPrivateKey(privateKey, rand = secureRandom) {
    switch (privateKey.constructor) {
        case ED25519PrivateKey:
            return new ED25519KeyExchange(rand);
        default:
            throw RangeError('unknown private key type');
    }
}

export { fromPrivateKey, fromPublicKey };
