'use strict';

var ecdh = require('../ecdh/ecdh.js');
var secretbox = require('./secretbox.js');
var serialization = require('./serialization.js');

class PublicKeyDecrypter {
    constructor(keyEx, prvKey) {
        Object.defineProperty(this, "_keyEx", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: void 0
        });
        Object.defineProperty(this, "_prvKey", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: void 0
        });
        this._keyEx = keyEx;
        this._prvKey = prvKey;
    }
    static FromPrivateKey(key) {
        return new this(ecdh.fromPrivateKey(key), key);
    }
    async decrypt(input) {
        const secretData = serialization.deserializePublicKeyEncryptedContent(input);
        const sharedSecret = await this._keyEx.SharedSecret(this._prvKey, secretData.pubKey);
        return secretbox.easyOpen(secretData.encryptedContent, sharedSecret);
    }
}

exports.PublicKeyDecrypter = PublicKeyDecrypter;
