'use strict';

var nacl = require('tweetnacl');
var keys = require('../keys.js');

const ED25519PublicKeyLen = 32;
class ED25519PublicKey {
    constructor(bytes) {
        Object.defineProperty(this, "curve", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: keys.KindED25519
        });
        Object.defineProperty(this, "bytes", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: void 0
        });
        if (bytes.length !== ED25519PublicKeyLen) {
            throw new RangeError('invalid public key length');
        }
        this.bytes = bytes;
    }
    async verify(message, sig) {
        return nacl.sign.detached.verify(message, sig, this.bytes);
    }
}

exports.ED25519PublicKey = ED25519PublicKey;
exports.ED25519PublicKeyLen = ED25519PublicKeyLen;
