'use strict';

var bip39 = require('@scure/bip39');
var english = require('@scure/bip39/wordlists/english');

function validate(mnemonic) {
    return bip39.validateMnemonic(mnemonic, english.wordlist);
}
function toSeed(mnemonic, password, byteLength) {
    if (!validate(mnemonic)) {
        throw new Error('Invalid mnemonic');
    }
    return bip39.mnemonicToSeedSync(mnemonic, password).slice(0, byteLength === undefined ? 32 : byteLength);
}

exports.toSeed = toSeed;
exports.validate = validate;
