'use strict';

function isPublicKeyEqual(a, b) {
    if (a.bytes.length !== a.bytes.length) {
        return false;
    }
    return a.bytes.every((value, index) => value === b.bytes[index]);
}
function isPublicKey(x) {
    if (typeof x !== 'object')
        return false;
    if (!(x.bytes instanceof Uint8Array))
        return false;
    if (!(x.verify instanceof Function))
        return false;
    if (typeof x.curve !== 'string')
        return false;
    return true;
}

exports.isPublicKey = isPublicKey;
exports.isPublicKeyEqual = isPublicKeyEqual;
