function partitionMailchainResults(calls) {
    const successes = [];
    const failures = [];
    for (const call of calls) {
        const { result, params } = call;
        if (isErrorMailchainResult(result))
            failures.push({ params, error: result.error });
        if (isSuccessMailchainResult(result))
            successes.push({ params, data: result.data });
    }
    return { successes, failures };
}
function isErrorMailchainResult(r) {
    return r.error != null;
}
function isSuccessMailchainResult(r) {
    return r.data != null;
}

export { partitionMailchainResults };
