import { isPublicKey, publicKeyToBytes } from '@mailchain/crypto';
import { encodeHex } from '@mailchain/encoding';

function encodeMailbox(mailbox) {
    let key;
    if (isPublicKey(mailbox))
        key = mailbox;
    else
        key = mailbox.identityKey;
    return encodeHex(publicKeyToBytes(key));
}

export { encodeMailbox };
