'use strict';

var crypto = require('@mailchain/crypto');
var encoding = require('@mailchain/encoding');

function encodeMailbox(mailbox) {
    let key;
    if (crypto.isPublicKey(mailbox))
        key = mailbox;
    else
        key = mailbox.identityKey;
    return encoding.encodeHex(crypto.publicKeyToBytes(key));
}

exports.encodeMailbox = encodeMailbox;
