'use strict';

var consts = require('./consts.js');

function createHeader(label, value, ...attrs) {
    return { label, value, attrs };
}
function createMessageIdHeader(label, ids, ...attrs) {
    return {
        label,
        value: {
            type: 'message-ids',
            ids,
        },
        attrs,
    };
}
function contentTypeBoundaryHeader(type, boundary) {
    return createHeader(consts.HEADER_LABELS.ContentType, `multipart/${type}`, ['boundary', boundary]);
}

exports.contentTypeBoundaryHeader = contentTypeBoundaryHeader;
exports.createHeader = createHeader;
exports.createMessageIdHeader = createMessageIdHeader;
