'use strict';

var consts = require('./consts.js');

/** Definition for the ordering the the headers. It is not something required by specification, but looks prettier. */
const HEADER_ORDERS = {
    [consts.HEADER_LABELS.MimeVersion]: 100,
    [consts.HEADER_LABELS.Date]: 90,
    [consts.HEADER_LABELS.MessageId]: 80,
    [consts.HEADER_LABELS.Subject]: 50,
    [consts.HEADER_LABELS.From]: 34,
    [consts.HEADER_LABELS.ReplyTo]: 33,
    [consts.HEADER_LABELS.To]: 32,
    [consts.HEADER_LABELS.Cc]: 31,
    [consts.HEADER_LABELS.Bcc]: 30,
    [consts.HEADER_LABELS.ContentType]: -100,
    [consts.HEADER_LABELS.ContentDisposition]: -101,
    [consts.HEADER_LABELS.ContentTransferEncoding]: -102,
    [consts.HEADER_LABELS.ContentId]: -103,
};
/** Compares two headers for their ordering by using the {@link HEADER_ORDERS}. */
function byHeaderOrder(a, b) {
    return (HEADER_ORDERS[b.label] ?? 0) - (HEADER_ORDERS[a.label] ?? 0);
}

exports.HEADER_ORDERS = HEADER_ORDERS;
exports.byHeaderOrder = byHeaderOrder;
