'use strict';

function isStringHeader(header) {
    return typeof header.value === 'string';
}
function isDateHeader(header) {
    return header.value instanceof Date;
}
function isAddressHeader(header) {
    return Array.isArray(header.value) && header.value.every((a) => typeof a.address === 'string');
}
function isMessageIdHeader(header) {
    return header.value.type === 'message-ids';
}

exports.isAddressHeader = isAddressHeader;
exports.isDateHeader = isDateHeader;
exports.isMessageIdHeader = isMessageIdHeader;
exports.isStringHeader = isStringHeader;
