import { decodeHexAny, encodeHex } from '@mailchain/encoding';
import { privateKeyFromBytes, privateKeyToBytes, publicKeyFromBytes, publicKeyToBytes } from '@mailchain/crypto';

function privateMessagingKeyFromHex(hex) {
    return privateKeyFromBytes(decodeHexAny(hex));
}
function privateMessagingKeyToHex(key) {
    return encodeHex(privateKeyToBytes(key));
}
function publicMessagingKeyFromHex(hex) {
    return publicKeyFromBytes(decodeHexAny(hex));
}
function publicMessagingKeyToHex(key) {
    return encodeHex(publicKeyToBytes(key));
}

export { privateMessagingKeyFromHex, privateMessagingKeyToHex, publicMessagingKeyFromHex, publicMessagingKeyToHex };
