'use strict';

var encoding = require('@mailchain/encoding');
var crypto = require('@mailchain/crypto');

function privateMessagingKeyFromHex(hex) {
    return crypto.privateKeyFromBytes(encoding.decodeHexAny(hex));
}
function privateMessagingKeyToHex(key) {
    return encoding.encodeHex(crypto.privateKeyToBytes(key));
}
function publicMessagingKeyFromHex(hex) {
    return crypto.publicKeyFromBytes(encoding.decodeHexAny(hex));
}
function publicMessagingKeyToHex(key) {
    return encoding.encodeHex(crypto.publicKeyToBytes(key));
}

exports.privateMessagingKeyFromHex = privateMessagingKeyFromHex;
exports.privateMessagingKeyToHex = privateMessagingKeyToHex;
exports.publicMessagingKeyFromHex = publicMessagingKeyFromHex;
exports.publicMessagingKeyToHex = publicMessagingKeyToHex;
