'use strict';

var configuration = require('@mailchain/internal/configuration');
var messagingKeys = require('@mailchain/internal/messagingKeys');

async function getPrivateMessagingKey(address, keyRing) {
    return messagingKeys.PrivateMessagingKeys.create(configuration.defaultConfiguration).getExportablePrivateMessagingKey(address, keyRing);
}

exports.getPrivateMessagingKey = getPrivateMessagingKey;
