import { EncodingTypes } from '@mailchain/encoding';
import { encodingByProtocol } from '@mailchain/addressing';

function getLatestProofParams(protocol, locale) {
    const addressEncoding = encodingByProtocol(protocol);
    return {
        AddressEncoding: addressEncoding,
        PublicKeyEncoding: EncodingTypes.Hex0xPrefix,
        Locale: locale,
        Variant: 'simple-v1',
    };
}
function getMailchainUsernameParams() {
    return {
        AddressEncoding: EncodingTypes.Utf8,
        PublicKeyEncoding: EncodingTypes.Hex0xPrefix,
        Locale: 'en',
        Variant: 'mailchain-username',
    };
}

export { getLatestProofParams, getMailchainUsernameParams };
