import { encodeHex } from '@mailchain/encoding';

const mailchainDeliveryConfirmationMessage = (deliveryRequestID) => {
    return Buffer.from(`\x11Mailchain delivery confirmation:\n${encodeHex(deliveryRequestID)}`, 'utf-8');
};
const signMailchainDeliveryConfirmation = (pk, deliveryRequestID) => {
    const msg = mailchainDeliveryConfirmationMessage(deliveryRequestID);
    return pk.sign(msg);
};

export { mailchainDeliveryConfirmationMessage, signMailchainDeliveryConfirmation };
