import { ErrorUnsupportedKey, KindED25519 } from '@mailchain/crypto';

async function signRawEd25519(signer, msg) {
    switch (signer.curve) {
        case KindED25519: {
            return signer.sign(msg);
        }
        default:
            throw new ErrorUnsupportedKey(signer.curve);
    }
}
async function verifyRawEd25519(key, msg, signature) {
    switch (key.curve) {
        case KindED25519:
            return key.verify(msg, signature);
        default:
            throw new ErrorUnsupportedKey(key.curve);
    }
}

export { signRawEd25519, verifyRawEd25519 };
