'use strict';

var encoding = require('@mailchain/encoding');
var addressing = require('@mailchain/addressing');

function getLatestProofParams(protocol, locale) {
    const addressEncoding = addressing.encodingByProtocol(protocol);
    return {
        AddressEncoding: addressEncoding,
        PublicKeyEncoding: encoding.EncodingTypes.Hex0xPrefix,
        Locale: locale,
        Variant: 'simple-v1',
    };
}
function getMailchainUsernameParams() {
    return {
        AddressEncoding: encoding.EncodingTypes.Utf8,
        PublicKeyEncoding: encoding.EncodingTypes.Hex0xPrefix,
        Locale: 'en',
        Variant: 'mailchain-username',
    };
}

exports.getLatestProofParams = getLatestProofParams;
exports.getMailchainUsernameParams = getMailchainUsernameParams;
