'use strict';

var encoding = require('@mailchain/encoding');

const mailchainDeliveryConfirmationMessage = (deliveryRequestID) => {
    return Buffer.from(`\x11Mailchain delivery confirmation:\n${encoding.encodeHex(deliveryRequestID)}`, 'utf-8');
};
const signMailchainDeliveryConfirmation = (pk, deliveryRequestID) => {
    const msg = mailchainDeliveryConfirmationMessage(deliveryRequestID);
    return pk.sign(msg);
};

exports.mailchainDeliveryConfirmationMessage = mailchainDeliveryConfirmationMessage;
exports.signMailchainDeliveryConfirmation = signMailchainDeliveryConfirmation;
